/*
 * Notify callback function for `set_mode'.
 */
void
view_win_set_mode_notify_callback(item, value, event)
	Panel_item	item;
	int		value;
	Event		*event;
{
	view_win_objects *ip = (view_win_objects *) xv_get(item, XV_KEY_DATA, INSTANCE);
	
	
	/* gxv_start_connections DO NOT EDIT THIS SECTION */

	if (value == 1)
	{
		segal.mode = MODE_REGISTER;
	}
	
	if (value == 0)
	{
		segal.mode = MODE_SEGMENT;
		if(img.loaded) xv_set(Paint_win_paint->win_paint,
			XV_SHOW, TRUE,
			NULL);
	}
	
	if (value == 1)
	{
		xv_set(Paint_win_paint->win_paint, XV_SHOW, FALSE, NULL);
	}
	
	if (value == 1)
	{
		xv_set(View_win->but_masks, XV_SHOW, FALSE, NULL);
	}
	
	if (value == 0)
	{
		xv_set(View_win->but_masks, XV_SHOW, TRUE, NULL);
	}
	
	if (value == 0)
	{
		xv_set(View_win->but_segment, XV_SHOW, TRUE, NULL);
	}
	
	if (value == 1)
	{
		xv_set(View_win->but_segment, XV_SHOW, FALSE, NULL);
	}
	
	if (value == 0)
	{
		xv_set(View_win->but_preferences, XV_SHOW, TRUE, NULL);
	}
	
	if (value == 1)
	{
		xv_set(View_win->but_preferences, XV_SHOW, FALSE, NULL);
	}
	
	if (value == 0)
	{
		xv_set(View_win->msg_not_implemented, XV_SHOW, FALSE, NULL);
	}
	
	if (value == 1)
	{
		xv_set(View_win->msg_not_implemented, XV_SHOW, TRUE, NULL);
	}
	
	/* gxv_end_connections */

}

/*
 * Notify callback function for `set_display'.
 */
void
view_win_set_display_notify_callback(item, value, event)
	Panel_item	item;
	unsigned int	value;
	Event		*event;
{
	view_win_objects *ip = (view_win_objects *) xv_get(item, XV_KEY_DATA, INSTANCE);
	short	i;
	LOGIC orig_image, orig_mask, orig_xy;
	
	orig_image = segal.disp_image;
	orig_mask = segal.disp_mask;
	orig_xy = segal.disp_xy;

	segal.disp_image = FALSE;
	segal.disp_mask = FALSE;
	segal.disp_xy = FALSE;

	for (i = 0; i < 3; i++) {
		/* gxv_start_connections DO NOT EDIT THIS SECTION */

		if (i == 0 && (value & 01))
		{
			segal.disp_image = TRUE;
			
		}
		
		if (i == 1 && (value & 01))
		{
			segal.disp_mask = TRUE;
			
		}
		
		if (i == 2 && (value & 01))
		{
			segal.disp_xy = TRUE;
			
		}
		
		/* gxv_end_connections */

		value >>= 1;
	}

	/* just to be safe ... */
	if(!segal.r3d) segal.disp_xy = FALSE;

	/* changes that need to be addressed ... */
	if(segal.disp_xy != orig_xy) view_setup();
	if(segal.disp_image != orig_image
	|| segal.disp_mask != orig_mask) redisplay_all();
}

